# Validate against json stored in VARIABLE
Then(/^validate the response against expected json "([^"]*)"$/) do |jsonExpected|

	jsonExpectedVar = eval(jsonExpected)
	@data_hash_expected = JSON.parse(jsonExpectedVar.encode!('UTF-8'))
 	expect(@data_hash).to eq(@data_hash_expected)
end


